import '../styles.css';
import {Swiper, SwiperSlide} from 'swiper/react';
import SwiperCore, { Autoplay,Navigation, Pagination} from 'swiper';
import 'swiper/swiper-bundle.css';

import PropTypes from 'prop-types'
import pandora from '../Pandora.jpg';

import DummyData from '../DummyData.json';

SwiperCore.use([Autoplay,Navigation, Pagination]);

const ComSwiper = ({id, slides, freeMode, tag, wrapperTag, navigation, slidesPerView, spaceBetween, onInit, onSlideChange, onReachEnd }) => {
   
const getData = () => {
  const data = DummyData

  return (
      data.map((slide) => (
          <div class="swiper-slide">

          <SwiperSlide key= {slide.key}>
              
         <img
        src = {slide.img.src}
        alt = {slide.img.alt}
        />
    </SwiperSlide>
        </div>
      ))
  );
};
   
    return (
        
        <Swiper className="mySwiper" id={id}
        tag={tag}
        wrapperTag={wrapperTag}
        navigation = {navigation}
        slidesPerView={slidesPerView}
        spaceBetween ={spaceBetween}
        onInit={onInit}
        onSlideChange={onSlideChange}
        onReachEnd={onReachEnd}
        freeMode={freeMode} 
        autoplay={{
  "delay": 2500,
  "disableOnInteraction": false
}}>


           {getData()}


        </Swiper>
        
    )
}

export default ComSwiper

Swiper.propTypes = {
    id: PropTypes.string,
    slides: PropTypes.array,
    tag: PropTypes.string,
    wrapperTag: PropTypes.string,
    slidesPerView: PropTypes.number,
    spaceBetween: PropTypes.number,
    onInit: PropTypes.func,
    onSlideChange: PropTypes.func,
    onReachEnd: PropTypes.func
}


